import { Subjects } from '../../subjects';
/**
 * @interface subject:string, data:object
 * @data data in interface
 * @itemStockChangeList list of item to be changed (itemID:string,quantity:number)
 * @source domain that triggered stock change
 * @message (optional) additional message
 * @props (optional) additional object
 */
export interface ItemStockChangeEvent {
    subject: Subjects.ItemStockChange;
    data: {
        itemStockChangeList: ItemStockChangeList[];
        source: string;
        message?: string;
        props?: Object;
    };
}
/**
 * @itemID id of Item that quantity is to be changed
 * @quantity amount of Item change. Can be Positive or Negative Integer
 */
interface ItemStockChangeList {
    itemID: string;
    quantity: number;
}
export {};
